import React, { useEffect } from 'react';
import { useState } from 'react';
import './index.css';
import MovieCard from './Movies';

const API_URL='https://www.omdbapi.com?apikey=5a7e3b84';

const moviee={
Poster: "https://m.media-amazon.com/images/M/MV5BMTgzNzkxMzk5Nl5BMl5BanBnXkFtZTgwMTQ2MzA2MDE@._V1_SX300.jpg",
Title: "Undefined",
Type: "movie",
Year: "2006",
imdbID: "tt1436480"};

const App=()=>{
  const [searchTerm,setSearchTerm]=useState([]);
  const[movies,setMovies]=useState([]);

  const Movies= async (title)=>{
    const response= await fetch(`${API_URL}&s=${title}`);
    const data= await response.json();
    setMovies(data.Search);
  }
useEffect(()=>{
  Movies('superman');
},[])
  return(
    <div className='app'>
      <h2>
        App

      </h2>
      <div className='search'>
      <input  value={searchTerm} onChange={(e)=>{setSearchTerm(e.target.value)}} placeholder='Sup'>

      </input>
      <button onClick={()=>Movies(searchTerm)}>Search</button>
      </div>
      <div className='container'>
      {
                movies?.length>0 
                ? (<div className="container">
                {/* <MovieCard movie1={movies[0]}></MovieCard> */}
                {movies.map((movie)=> (<MovieCard movie = {movie}></MovieCard>))}
            </div>
            ):(
                <div className="empty"> 
                    <h2>No movies Found!</h2>
                </div>
)
            } 

      </div>

    </div>
  )


}
export default App;
