    return(
      <>
      <h1>Name: John</h1>
      <h1>Last Name: Doe</h1>
      <h1>Age: 30</h1>
      </>
    )
  }
  return (
    <div className="App">
      <Person></Person>
      <Person></Person>

    </div>
  );
}
export default App;
